import torch
from typing import Optional
from pylantern.tensors.manifold_tensor import ManifoldTensor

class ManifoldGradient:
    """Gradient computation on curved manifolds with observer bounds"""

    @staticmethod
    def compute(tensor: ManifoldTensor, scalar_field: Optional[ManifoldTensor] = None) -> ManifoldTensor:
        """Compute manifold gradient respecting curvature and observer bounds"""
        if not tensor.requires_grad:
            raise RuntimeError("Input tensor must have requires_grad=True to compute gradient.")

        if scalar_field is None:
            # If no scalar_field is provided, assume we want the gradient of the tensor itself
            # This is a simplified approach; a true manifold gradient requires a scalar function.
            # For now, we'll return the Euclidean gradient transformed by the inverse metric.
            if tensor.grad is None:
                # If grad is None, it means no backward pass has been performed yet.
                # We can return a zero tensor of the same shape.
                return ManifoldTensor(torch.zeros_like(tensor.data), manifold=tensor.manifold, observer_id=tensor.observer_id)

            euclidean_grad = tensor.grad.data
        else:
            # Compute gradient of scalar_field with respect to tensor.data
            # This assumes scalar_field is a scalar value derived from operations involving tensor.
            if scalar_field.data.numel() != 1:
                raise ValueError("scalar_field must be a scalar ManifoldTensor to compute its gradient.")

            euclidean_grad = torch.autograd.grad(scalar_field.data, tensor.data, retain_graph=True)[0]

        # Transform Euclidean gradient to manifold gradient using the inverse metric tensor
        # grad_manifold = g_inv * grad_euclidean
        metric = tensor.manifold.metric_tensor(tensor.data)
        inv_metric = torch.inverse(metric)

        # Ensure dimensions match for matrix multiplication
        if euclidean_grad.dim() == 1:
            manifold_grad_data = torch.matmul(inv_metric, euclidean_grad.unsqueeze(-1)).squeeze(-1)
        else:
            # Handle batch dimensions if necessary, for now assume 1D gradient
            raise NotImplementedError("ManifoldGradient.compute only supports 1D gradients for now.")

        # Apply observer's measurement protocol
        measured_manifold_grad_data = tensor.observer_id.measure(manifold_grad_data)

        return ManifoldTensor(measured_manifold_grad_data, manifold=tensor.manifold, observer_id=tensor.observer_id)

    @staticmethod
    def divergence(vector_field: ManifoldTensor) -> ManifoldTensor:
        """Compute divergence of vector field on manifold"""
        # Placeholder for divergence computation
        raise NotImplementedError("Divergence computation not yet implemented.")

import torch
from typing import Optional
from pylantern.tensors.manifold_tensor import ManifoldTensor

class ObserverDerivative:
    """Compute derivatives that respect observer measurement boundaries"""

    @staticmethod
    def compute(tensor: ManifoldTensor, direction: Optional[torch.Tensor] = None, order: int = 1) -> ManifoldTensor:
        """Compute observer-bounded derivative (orders 1 and 2 supported)"""
        if not tensor.requires_grad:
            raise RuntimeError("Input tensor must have requires_grad=True to compute derivative.")

        if order == 1:
            # Determine the scalar output for gradient computation
            # Sum the tensor elements to create a scalar for autograd.grad
            scalar_for_grad = tensor.sum()

            # Compute the gradient of scalar_for_grad with respect to tensor.data
            grad_outputs = torch.autograd.grad(scalar_for_grad, tensor.data, retain_graph=True, allow_unused=True)
            raw_gradient_data = grad_outputs[0] if grad_outputs and grad_outputs[0] is not None else torch.zeros_like(tensor.data)

            if direction is None:
                # First order derivative (gradient)
                raw_derivative_data = raw_gradient_data
            else:
                # Directional derivative: grad(f) . direction
                if raw_gradient_data.shape != direction.shape:
                    raise ValueError("Gradient and direction must have the same shape for dot product.")
                raw_derivative_data = torch.dot(raw_gradient_data.flatten(), direction.flatten())

            # Apply observer's measurement protocol
            measured_derivative_data = tensor.observer_id.measure(raw_derivative_data)
            return ManifoldTensor(measured_derivative_data, manifold=tensor.manifold, observer_id=tensor.observer_id)

        elif order == 2:
            raise NotImplementedError("Second order derivatives are not yet implemented.")
        else:
            raise ValueError("Order must be 1 or 2.")

