import torch
from pylantern.symbolic_constants import CurvatureType, SymbolicConstant

class RiemannianManifold(ABC):
    """Base class for Riemannian manifolds with observer-bounded geometry"""

    def __init__(self, dimension: int, observer_resolution: float = 0.01):
        self.dim = dimension
        self.observer_res = observer_resolution

    @abstractmethod
    def metric_tensor(self, point: torch.Tensor) -> torch.Tensor:
        """Compute metric tensor g_ij at given point"""
        pass

    @abstractmethod
    def christoffel_symbols(self, point: torch.Tensor) -> torch.Tensor:
        """Compute connection coefficients Î“^k_ij"""
        pass

    def riemann_curvature(self, point: torch.Tensor) -> torch.Tensor:
        """Compute Riemann curvature tensor R^i_jkl"""
        raise NotImplementedError

    def scalar_curvature(self, point: torch.Tensor) -> torch.Tensor:
        """Compute scalar curvature R = g^ij R_ij"""
        raise NotImplementedError


    def classify_curvature(self, point: torch.Tensor, threshold: float = 1e-6) -> SymbolicConstant:
        """Classify local curvature type at given point"""
        s_curvature = self.scalar_curvature(point)
        if torch.abs(s_curvature) < threshold:
            return CurvatureType.ZERO
        elif s_curvature > 0:
            return CurvatureType.POSITIVE
        else:
            return CurvatureType.NEGATIVE

