class ManifoldModule(nn.Module):
    """Base class for neural network modules that operate on curved manifolds"""

    def __init__(self):
        super().__init__()
        self._manifold: Optional[RiemannianManifold] = None
        self.observer_id: str = "module"
        self._manifold_parameters: List[ManifoldTensor] = []

    def set_manifold(self, manifold: RiemannianManifold, observer_id: str = "module"):
        self._manifold = manifold
        self.observer_id = observer_id

    def manifold_parameters(self) -> Iterator[ManifoldTensor]:
        return iter(self._manifold_parameters)

    def add_manifold_parameter(self, name: str, param: ManifoldTensor):
        self.register_parameter(name, torch.nn.Parameter(param.data))
        self._manifold_parameters.append(param)

    def get_curvature_stats(self) -> Dict[str, float]:
        """Get curvature statistics for all manifold parameters"""
        if not self._manifold_parameters:
            return {"mean_curvature": 0.0, "std_curvature": 0.0, "max_curvature": 0.0, "min_curvature": 0.0}

        curvatures = []
        for param in self._manifold_parameters:
            try:
                # Ensure local_curvature returns a scalar tensor
                curvatures.append(param.local_curvature().item())
            except NotImplementedError:
                # Handle cases where scalar_curvature might not be implemented for the manifold
                continue
            except Exception as e:
                # Catch other potential errors during curvature computation
                print(f"Error computing local curvature for a parameter: {e}")
                continue

        if not curvatures:
            return {"mean_curvature": 0.0, "std_curvature": 0.0, "max_curvature": 0.0, "min_curvature": 0.0}

        curvatures_tensor = torch.tensor(curvatures)
        return {
            "mean_curvature": torch.mean(curvatures_tensor).item(),
            "std_curvature": torch.std(curvatures_tensor).item(),
            "max_curvature": torch.max(curvatures_tensor).item(),
            "min_curvature": torch.min(curvatures_tensor).item()
        }

import torch
import torch.nn as nn
from pylantern.neural_network_modules.manifold_module import ManifoldModule

class ManifoldSequential(ManifoldModule):
    """Sequential container for manifold modules"""

    def __init__(self, *modules: ManifoldModule):
        super().__init__()
        self.modules_list = nn.ModuleList(modules)

    def forward(self, x: torch.Tensor) -> torch.Tensor:
        """Forward through all modules"""
        for module in self.modules_list:
            x = module(x)
        return x

