from typing import Dict

class BoundaryObserver(Observer):
    """An observer that enforces explicit magnitude and precision bounds."""

    def __init__(self, bounds: Dict[str, float], **kwargs):
        super().__init__(**kwargs)
        self.bounds = bounds

    def measure(self, tensor_data: torch.Tensor) -> torch.Tensor:
        """Apply the observer's measurement protocol to raw tensor data."""
        if 'magnitude' in self.bounds:
            tensor_data = torch.clamp(tensor_data, -self.bounds['magnitude'], self.bounds['magnitude'])
        if 'precision' in self.bounds:
            tensor_data = torch.round(tensor_data / self.bounds['precision']) * self.bounds['precision']
        return tensor_data

