from pylantern.symbolic_constants import ObserverComposition, SymbolicConstant

class MetaObserver(Observer):
    """Observer that coordinates multiple sub-observers for multi-scale measurement"""

    def __init__(self, sub_observers: List[Observer], composition_mode: SymbolicConstant = ObserverComposition.HIERARCHICAL, **kwargs):
        super().__init__(**kwargs)
        self.sub_observers = sub_observers
        self.composition_mode = composition_mode

    def measure(self, tensor_data: torch.Tensor) -> torch.Tensor:
        """Apply the observer's measurement protocol to raw tensor data."""
        measurements = [obs.measure(tensor_data) for obs in self.sub_observers]
        return self.compose_measurements(measurements)

    def compose_measurements(self, measurements: List[torch.Tensor]) -> torch.Tensor:
        """Compose multiple observer measurements into unified result"""
        if self.composition_mode == ObserverComposition.CONSENSUS:
            return torch.mean(torch.stack(measurements), dim=0)
        elif self.composition_mode == ObserverComposition.HIERARCHICAL:
            # Simple hierarchical composition: just take the first observer's measurement
            return measurements[0]
        else:
            raise NotImplementedError

