    "test_phi_convergence_under_drift": {
      "description": "Simulates symbolic drift on a curved manifold and evaluates \u03c6-alignment convergence patterns.",
      "path": "tests/test_phi_convergence_under_drift.py",
      "code": "\"\"\"\nTest: \u03c6-Convergence Under Drift\n-------------------------------\nSimulates a symbolic tensor evolving on a curved manifold,\nwith periodic noise injection. Tracks \u03c6-alignment and flow\nto demonstrate symbolic stability and convergence.\n\"\"\"\n\nimport torch\nfrom pylantern import (\n    PoincareBall,\n    SpectralObserver,\n    ManifoldTensor,\n    SymbolicGradientFlow,\n    EmergenceLogger,\n    phi_attractor_proximity\n)\n\nmanifold = PoincareBall(dimension=2)\nobserver = SpectralObserver()\ntracker = SymbolicGradientFlow(manifold, observer)\nlogger = EmergenceLogger()\n\ntarget_phi = 1.618\ntensor = ManifoldTensor(torch.tensor([1.5, 1.5], requires_grad=True), manifold, observer)\noptimizer = torch.optim.SGD([tensor.data], lr=0.05)\n\ntensor_history = []\n\nfor epoch in range(50):\n    if epoch % 10 == 0:\n        tensor.data.data += torch.randn_like(tensor.data) * 0.15\n\n    loss = (tensor.data.norm() - 1.0).abs()\n    optimizer.zero_grad()\n    loss.backward()\n    optimizer.step()\n\n    historical_tensor = ManifoldTensor(tensor.data.detach().clone().requires_grad_(True), manifold, observer)\n    tensor_history.append(historical_tensor)\n\n    grad = tensor.observer_gradient()\n    flow = tracker.track_flow_patterns([grad])\n\n    phi_score = phi_attractor_proximity(\n        tensor_history,\n        attractor_threshold=target_phi,\n        proximity_window=min(32, len(tensor_history))\n    )[\"proximity_score\"]\n\n    logger.log_epoch(\n        epoch=epoch,\n        model_params=[tensor.data.tolist()],\n        loss_info={\"phi_alignment\": phi_score},\n        flow=flow,\n        convergence=tracker.predict_convergence(grad)\n    )\n\nprint(\"\u2705 Drift convergence test complete.\")",
